import "./cart-icon.styles.scss";
import { ReactComponent as ShopingIcon } from "../../assets/shopping-bag.svg";
import { useContext } from "react";
import { CartContext } from "../../context/cart.context";



const sumItemQuantities =(cartItems) =>{
  return cartItems.reduce((n, {quantity})=>n + quantity)
}

const CartIcon = () => {
  const { setIsCartOpen, isCartOpen, cartItems } = useContext(CartContext);

  const sumQuantities = sumItemQuantities(cartItems)

  const handleOnClick = () => {
    setIsCartOpen(!isCartOpen);
  };
  return (
    <div className="cart-icon-container" onClick={handleOnClick}>
      <ShopingIcon className="shopping-icon" />
      <span className="item-count">{0}</span>
    </div>
  );
};

export default CartIcon;
